'use strict';

// alert("connected!");

document.getElementById('addpost').addEventListener('submit', addPost);

function addPost(e) {
    e.preventDefault();

    var firstname = document.getElementById('firstname').value;
    var lastname = document.getElementById('lastname').value;
    var othername = document.getElementById('othername').value;
    var email = document.getElementById('email').value;
    var phonenumber = document.getElementById('phone').value;
    var passportUrl = document.getElementById('file').value;
    var password = document.getElementById('password').value;
    var result = document.getElementById('result');
    var responseStatus = false;
    console.log('hhhhhh');

    fetch('https://trustpolitico.herokuapp.com/api/v1/auth/signup', {
        method: 'POST',
        headers: {
            'Accept': 'application/json, text/plain, */*',
            'Content-type': 'application/json'
        },
        body: JSON.stringify({
            passportUrl: passportUrl,
            firstname: firstname,
            lastname: lastname,
            othername: othername,
            email: email,
            phonenumber: phonenumber,
            password: password
        })

    }).then(function (res) {
        if (res.ok) {
            responseStatus = true;
            result.innerHTML = "Signup successful!";
            result.style.color = "green";
        }
        return res.json();
    }).then(function (res) {
        console.log(res.user);
        if (!responseStatus) {
            result.innerHTML = res.error;
            result.style.color = "red";
        } else {

            window.localStorage.setItem('token', res.data[0].token);
            window.location.href = '/views/parties.html';
            console.log(res);
        }
    });
}
